#include <iostream>#include <cmath>using namespace std;int min(int x, int y){	int z;	if (x > y)	{		z = y;	}	else	{		z = x;	}	return z;}int max(int m, int n){	int z;	if (m > n)	{		z = m;	}	else	{		z = n;	}	return z;}int main(){	int a, b, p, q, i;	double fenshu, zhenshu, answer;	cin >> a >> b;	p = min(a, b);	q = max(a, b);	fenshu = double(q) / p - q / p;								//cout << fenshu <<endl;	for (i = 1; (i * double(q) / p - i * q / p) - int((i * double(q) / p - i * q / p)) != 0; i = i + 1)	{		i = i;	}									//cout << i << endl;	answer = p / i;	cout << answer;	return 0;}